import React, {useState} from 'react';
import {v4 as uuidv4} from 'uuid';

const Form = ({titleInput, setTitleInput, todos, setTodos}) => {
  const onInputChange = (event) => {
    setTitleInput(event.target.value);
  };
  const onFormSubmit = (event) => {
    event.preventDefault();
    setTodos([...todos, {id: uuidv4(), title: titleInput, isDone: false}]);
    setTitleInput('');
  };
  return (
    <form className='todo-form' onSubmit={onFormSubmit}>
      <div className='input'>
        <div className='title'>제목</div>
        <input type='text' placeholder='enter' className='task-input' value={titleInput} name='text' onChange={onInputChange} />
        <button className='button-add' type='submit'>
          추가하기
        </button>
      </div>
    </form>
  );
};

export default Form;
