const Done = ({todos, setTodos}) => {
  console.log(todos);
  const handleComplete = (todo) => {
    console.log('complete');
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {...item, completed: !item.completed};
        }
        return item;
      })
    );
  };
  const handleDelete = ({id}) => {
    console.log('delete');
    setTodos(todos.filter((todo) => todo.id !== id));
  };
  return (
    <div>
      {todos.map((todo) => {
        // if (todo.completed === true) {
        console.log(true);
        <li className='todo-list' key={todo.id}>
          <input type='text' value={todo.title} className='list' onChange={(event) => event.preventDefault()} />
          <div className='buttons'>
            <button className='button-delete' onClick={() => handleDelete(todo)}>
              삭제하기
            </button>
            <button className='button-complete' onClick={() => handleComplete(todo)}>
              취소
            </button>
          </div>
        </li>;
      })}
    </div>
  );
};

export default Done;
