      todos.map((item) => {
        if (item.id === todo.id) {
          return {...item, isDone: !item.isDone};
        }
        return item;
      })
    );
  };

  const handleDelete = ({id}) => {
    setTodos(todos.filter((todo) => todo.id !== id));
  };

