    let resultDone = [];
    for (let todo of arr) {
      if (todo.isDone === trueOrFalse) {
        resultDone.push(
          <li className='todo-list' key={todo.id}>
            <div>{todo.title}</div>
            <div>{todo.context}</div>

            <div className='buttons'>
              <button className='button-delete' onClick={() => handleDelete(todo)}>
                ì‚­ì œí•˜ê¸°
              </button>
              <button className='button-complete' onClick={() => handleComplete(todo)}>
                {trueOrFalse ? 'ì·¨ì†Œ' : 'ì™„ë£Œ'}
              </button>
            </div>
          </li>
        );
      }
    }
    return resultDone;
  };

  return (
    <div className='list-container'>
      <h2 className='list-title'>Working..ðŸ”¥</h2>
      {checkDone(todos, false)}
      <h2 className='list-title'>Done</h2>
      {checkDone(todos, true)}
    </div>
  );
};

export default TodoList;
