    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return {...item, isDone: !item.isDone};
        }
        return item;
      })
    );
    doneTodos = todos;
    console.log(doneTodos);
    setTodos(todos.filter((todo) => todo.isDone === true));
    // setTodos([...todos, {id: uuidv4(), title: input, isDone: false}]);
    setDoneTodos(...todos.filter((todo) => todo.isDone === false));
  };

